import { actionTypes } from './actions'

export const exampleInitialState = {
  leaguesData: null,
  seasonsData: null,
  fixtureData: null,
  lineupData: null,
  error: false,
  leagueInfo: {
    status: false,
    id: null
  },
  seasonInfo: {
    status: false,
    id: null
  },
  leagueInfo: {
    status: false,
    id: null
  },
}
const [seasonStatus, setSeasonStatus] = useState(false)
  const [seasonId, setSeasonId] = useState(null)

  const [fixtureStatus, setFixtureStatus] = useState(false)
  const [fixtureId, setFixtureId] = useState(null)
function reducer(state = exampleInitialState, action) {

  switch (action.type) {
    case actionTypes.FAILURE:
      return {
        ...state,
        ...{ error: action.error },
      }

    // case actionTypes.CHANGE_LEAGUE_INFO:
    //   return {
    //     ...state,
    //     ...{ leagueInfo: {status: true, id: action.payload }},
    //   }

    case actionTypes.LOAD_LEAGUES_SUCCESS:
      return {
        ...state,
        ...{ leaguesData: action.data },
      }

    case actionTypes.LOAD_FIXTURES_SUCCESS:
      return {
        ...state,
        ...{ fixtureData: action.data },
      }

    case actionTypes.LOAD_LINEUP_SUCCESS:
      return {
        ...state,
        ...{ lineupData: action.data },
      }

    case actionTypes.LOAD_SEASONS_SUCCESS:
      return {
        ...state,
        ...{ seasonsData: action.data, leagueInfo: {status: true, id: action.payload } },
      }

    default:
      return state
  }
}

export default reducer
