import React, {useState} from 'react'
import { connect, useSelector } from 'react-redux'
import map from 'lodash/map'
import LeagueSeasons from './LeagueSeasons'
import FixtureLineup from './FixtureLineup'
import SeasonFixture from './SeasonFixture'
import {loadSeasons, changeLeagueInfo} from '../actions'

const League = ({error, leaguesData, title, dispatch}) => {


  const leagueStatus = useSelector(state => state.leagueInfo)
  const [seasonStatus, setSeasonStatus] = useState(false)
  const [seasonId, setSeasonId] = useState(null)

  const [fixtureStatus, setFixtureStatus] = useState(false)
  const [fixtureId, setFixtureId] = useState(null)

  const leagueLink = (id) => {
    if (id != leagueId) {
      dispatch(loadSeasons(id))
      // dispatch(changeLeagueInfo(id))
    }
  }

  return (
    <div className="main">
      <div>
        <h1>{title}</h1>
        <div className="all_tournaments">
          {leaguesData && map(leaguesData.response, (item, key) => {
              return <a key={key} onClick={() => leagueLink(item.league.id)} >{item.league.name}</a>;
            })
          }
        </div>

        {error && <p style={{ color: 'red' }}>Error: {error.message}</p>}
      </div>

      {leagueStatus && (
         <LeagueSeasons leagueId = {leagueId} title="Seasons"/>
      )} }

      {/* {seasonStatus && (
         <SeasonFixture seasonId = {seasonId} title="Matches"/>
      )} }

      {fixtureStatus && (
        <FixtureLineup fixtureId = {fixtureId}/>
      )} */}
    </div>
  )
}

export default connect((state) => state)(League)
