import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux'
import head from 'lodash/head'
import concat from 'lodash/concat'
import forEach from 'lodash/forEach'
import last from 'lodash/last'
import SoccerLineUp from 'react-soccer-lineup'
import {lineupTempData} from '../temp/tempData'

const FixtureLineup = (fixtureId) => {
    const [lineupData, setLineupData] = useState(useSelector(state => state.lineupData))
    // const [homeSquad, setHomeSquad] = useState(null)
    // const [awaySquad, setAwaySquad] = useState(null)
    const homeSquad = null
    if (lineupData === null){
        setLineupData(lineupTempData)
        console.log(lineupData)
    }
    const getStartXI = (startXI) => {
        let squad = { "gk": {}, "df": [], "cm": [], "fw": [] }
        forEach(startXI, item => {
            switch(item.player.pos){
                case "G":
                    squad.gk = item
                    break
                case "D":
                    squad.df = concat(squad.df, item)
                    break
                case "M":
                    squad.cm = concat(squad.cm, item)
                    break
                case "F":
                    squad.fw = concat(squad.fw, item)
                    break
                default:
                    break
            }
        })
        return squad
    }

    useEffect(() => {         
        homeSquad = getStartXI(head(lineupData.response).startXI)
        awaySquad = getStartXI(last(lineupData.response).startXI)

    }, [])


    const homeTeam = {
        "color": "red",
        "squad": homeSquad
    }
    const awayTeam = {
        "color": "blue",
        "squad": awaySquad
    }

    return (
        <SoccerLineUp
            size={ "small" }
            color={ "lightseagreen" }
            pattern={ "circles" }
            homeTeam={ homeTeam }
            awayTeam={ awayTeam }
        />
    )

}
export default FixtureLineup
