import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux'
import head from 'lodash/head'
import concat from 'lodash/concat'
import forEach from 'lodash/forEach'
import last from 'lodash/last'
import SoccerLineUp from 'react-soccer-lineup'
import {lineupTempData} from '../temp/tempData'

const FixtureLineup = (fixtureId) => {
    const [lineupData, setLineupData] = useState(useSelector(state => state.lineupData))
    const [homeSquad, setHomeSquad] = useState(null)
    const [awaySquad, setAwaySquad] = useState(null)
    // let homeSquad = { "gk": {}, "df": [], "cm": [], "fw": [] }
    // let awaySquad = { "gk": {}, "df": [], "cm": [], "fw": [] }
    let homeTeam = {
        "color": "red",
        "squad": { "gk": {}, "df": [], "cm": [], "fw": [] }
    }
    let awayTeam = {
        "color": "blue",
        "squad": { "gk": {}, "df": [], "cm": [], "fw": [] }
    }
    if (lineupData === null){
        setLineupData(lineupTempData)
    }
    const getStartXI = (startXI, team) => {
        console.log(team)
        // let squad = { "gk": {}, "df": [], "cm": [], "fw": [] }
        forEach(startXI, item => {
            switch(item.player.pos){
                case "G":
                    team.squad.gk = item
                    break
                case "D":
                    team.squad.df = concat(team.squad.df, item)
                    break
                case "M":
                    team.squad.cm = concat(team.squad.cm, item)
                    break
                case "F":
                    team.squad.fw = concat(team.squad.fw, item)
                    break
                default:
                    break
            }
        })
        return team
    }

    useEffect(() => {         
        homeTeam = getStartXI(head(lineupData.response).startXI, homeTeam)
        awayTeam = getStartXI(last(lineupData.response).startXI, awayTeam)
        console.log(homeTeam)
    }, [])




    return (
        <SoccerLineUp
            size={ "small" }
            color={ "lightseagreen" }
            pattern={ "circles" }
            homeTeam={ homeTeam }
            awayTeam={ awayTeam }
        />
    )

}
export default FixtureLineup
